package persistence;import Product.IDisplayable;import Product.PictureMedia;import org.apache.commons.net.ftp.FTPClient;import java.sql.*;import java.util.Set;/** * Created by Vedsted on 18-05-2017. */public class DAMDBPersistence implements IDAMPersistence {    private Connection connection;    private FTPClient client;    public DAMDBPersistence(String connectionString, String username, String password) {        try {            this.connection = DriverManager.getConnection(connectionString, username, password);        } catch (SQLException e) {            e.printStackTrace();        }    }    @Override    public IDisplayable fetchMedia(String mediaID) {        IDisplayable media = null;        try {            Statement st = connection.createStatement();            System.out.println("fetching from db");            String query = "SELECT * FROM media WHERE id = " + mediaID +";";            ResultSet rs = st.executeQuery(query);            while (rs.next()) {                System.out.println(rs.getInt("id") + rs.getString("path"));                media = new PictureMedia(rs.getInt("id"), rs.getString("path"));            }        } catch (SQLException e) {            e.printStackTrace();        }        return media;    }    @Override    public Set<IDisplayable> fetchMediaOverview() {        return null;    }}