package ecomweb.WEBBusinessLayer;import Product.DetailedProduct;import java.io.*;/** * Created by Vedsted on 24-04-2017. */public class WebManager implements IWebManager {    private ClientHandler client;    public WebManager() {        client = new ClientHandler();    }    @Override    public DetailedProduct getProduct(int productID) {        try {            return client.getProduct(productID);        } catch (IOException e) {            e.printStackTrace();        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        return null;    }}