package ecomweb.WEBBusinessLayer;import Product.DetailedProduct;import ecomweb.network.ClientHandler;import ecomweb.network.FTPTool;import java.io.*;/** * Created by Vedsted on 24-04-2017. */public class WebManager implements IWebManager {    private ClientHandler client;    private FTPTool ftpTool;    public WebManager() {        client = new ClientHandler();        ftpTool = new FTPTool("", "");    }    @Override    public DetailedProduct getProduct(int productID) {        try {            return client.getProduct(productID);        } catch (IOException e) {            e.printStackTrace();        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        return null;    }    @Override    public String getMediaPath(String mediaPath) {        String path = "htdocs" + mediaPath;        System.out.println("manager path: " + path);        return ftpTool.retrieveFile(path.trim());    }}