package ecomweb.WEBBusinessLayer;import Networking.DetailedProductFrame;import Product.DetailedProduct;import ecomweb.Client.ClientHandler;import java.io.*;/** * Created by Vedsted on 24-04-2017. */public class WebManager implements IWebManager {    private ClientHandler client;    public WebManager() {        client = new ClientHandler();    }    @Override    public DetailedProduct getProduct(int productID) {        try {            DetailedProductFrame detailedProductFrame = client.getProduct(productID);            switch(detailedProductFrame.getStatusCode()){                case OK:                    return detailedProductFrame.getProduct();                case ERRORINPIM:                    return null;                case PRODUCTNOTFOUND:                    return null;                default:                    return null;            }        } catch (IOException e) {            e.printStackTrace();        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        return null;    }}